define(['jquery', 'swiper'], function ($, Swiper) {
    'use strict';

    return function (config, element) {
        $(document).ready(function () {
            new Swiper('.top_product_slider', {
                slidesPerView: 3,
                spaceBetween: 20,
                navigation: {
                    nextEl: '.top_product-next',
                    prevEl: '.top_product-prev',
                },
                loop: true,
                breakpoints: {
                    1024: { slidesPerView: 3 },
                    768: { slidesPerView: 2 },
                    480: { slidesPerView: 1 }
                }
            });
        });
    };
});
